urlset:
{%- for file in pages %}
    {%- if not file.page.is_link %}
        {%- if file.page.canonical_url %}
            {%- set loc_content = file.page.canonical_url | e %}
        {%- elif file.page.abs_url %}
            {%- set loc_content = file.page.abs_url | e %}
        {%- else %}
            {%- set loc_content = config.site_url ~ file.url %}
        {%- endif %}

        {{ loc_content }}
        {%- if file.page.update_date %}
        {{file.page.update_date}}
        {%- endif %}
        daily
        {%- for locale, i18n_file in file.alternates.items() %}
            {%- if i18n_file %}
                {%- if i18n_file.page.canonical_url %}
                    {%- set link_href = i18n_file.page.canonical_url | e %}
                {%- else %}
                    {%- set link_href = config.site_url ~ i18n_file.url %}
                {%- endif %}

            {%- endif %}
        {%- endfor %}

    {%- endif %}
{%- endfor %}
